import { Component, OnInit, ViewChild } from '@angular/core';
import { AdminService } from '../Services/admin.service';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-student-list',
  templateUrl: './student-list.component.html',
  styleUrls: ['./student-list.component.scss']
})
export class StudentListComponent implements OnInit {

  dataSource:any;
  displayedColumns: string[] = ["firstName","lastName","email", "phoneNumber","Actions"];

constructor(private adminService:AdminService){

}

@ViewChild(MatPaginator, { static: true }) paginator: MatPaginator | undefined;
  ngOnInit(): void {
this.getAllStudent()
  }
  getAllStudent() {
this.adminService.getAllStudent().subscribe(
  (data:any) => {
    console.log(data);

    this.dataSource = new MatTableDataSource(data);
    this.dataSource.paginator = this.paginator;
  },
  (err:any) => {


console.log(err);

  });

  }

  handleDeleteAction(values: any){
    console.log(values.id);

    Swal.fire({
      title: 'Are you sure?',
      text: 'Are you sure you want to delete this Student?',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Yes, delete it!'
    }).then((result) => {
      if (result.isConfirmed) {
        // User clicked "Yes", implement company deletion here
        console.log('Deleting company...');
        this.deleteStudent(values.id);
      } else {
        // User clicked "Cancel" or closed the dialog
        console.log('Deletion canceled.');
      }
    });
  }



        deleteCompany(id: any) {
          this.adminService.deleteCompany(id).subscribe(
      (reponse:any)=>{

    Swal.fire("Company deleted successfully")
      this.getAllStudent();




      },
      (error:any)=>{
        Swal.fire("Error deleting Please try again")
        console.log(error);



      console.log(error);


      }

          );
        }
}
<mat-card style="margin-top: 50px;">
    <b>
      <span>List Of Company</span>

    </b>
  </mat-card>
    <hr />


  <div class="responsive_table">
    <mat-table [dataSource]="dataSource" matSort class="mat-elevation-z8">

      <!-- ID Column -->


      <!-- User Column -->


      <!-- Course Column -->
      <ng-container matColumnDef="firstName">
        <mat-header-cell *matHeaderCellDef mat-sort-header>firstName</mat-header-cell>
        <mat-cell *matCellDef="let row">{{ row.firstName}}</mat-cell>
      </ng-container>
      <!-- Course price -->
      <ng-container matColumnDef="lastName">
        <mat-header-cell *matHeaderCellDef mat-sort-header>lastName</mat-header-cell>
        <mat-cell *matCellDef="let row">{{ row.lastName }}</mat-cell>
      </ng-container>
      <ng-container matColumnDef="email" >
        <mat-header-cell *matHeaderCellDef mat-sort-header>email</mat-header-cell>
        <mat-cell *matCellDef="let row">{{ row.email }}</mat-cell>
      </ng-container>
      <!-- Enrollment Date Column -->
      <ng-container matColumnDef="phoneNumber">
        <mat-header-cell *matHeaderCellDef mat-sort-header>phone Number</mat-header-cell>
        <mat-cell *matCellDef="let row">{{ row.phoneNumber  }}</mat-cell>
      </ng-container>

      <!-- Completion Date Column -->


      <!-- Actions Column -->
      <ng-container matColumnDef="Actions">
        <mat-header-cell *matHeaderCellDef>Actions</mat-header-cell>
        <mat-cell *matCellDef="let row"> 

        <button
        mat-icon-button
        color="primary"
        matTooltip="View"
        class="edit-button"

      >
        <mat-icon>visibility</mat-icon>
      </button>
      <button
            mat-icon-button
            color="primary"
            matTooltip="Edit"
             class="edit-button"

          >
            <mat-icon>edit</mat-icon>
          </button>

        <button
        mat-icon-button
        matTooltip="Delete"
        class="delete-button"
      >
        <mat-icon>delete</mat-icon>
      </button>
    </mat-cell>
      </ng-container>

      <mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>
      <mat-row *matRowDef="let row; columns: displayedColumns;"></mat-row>
    </mat-table>

      <mat-paginator [pageSizeOptions]="[5, 25, 50]" [pageSize]="5"></mat-paginator>

    </div>
