import { Component, OnInit, ViewChild } from '@angular/core';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { AdminService } from '../Services/admin.service';
import { MatDialog, MatDialogConfig } from '@angular/material/dialog';
import { ConfirmationComponent } from '../dialog/confirmation/confirmation.component';

@Component({
  selector: 'app-company-list',
  templateUrl: './company-list.component.html',
  styleUrls: ['./company-list.component.scss']
})
export class CompanyListComponent implements OnInit {

  dataSource:any;
  displayedColumns: string[] = ["name","email", "phoneNumber","Actions"];

constructor(private adminService:AdminService,
  private dialog: MatDialog,

  ){

}

@ViewChild(MatPaginator, { static: true }) paginator: MatPaginator | undefined;
  ngOnInit(): void {
this.getAllCompany()
  }
  getAllCompany() {
this.adminService.getAllCompany().subscribe(
  (data:any) => {
    console.log(data);

    this.dataSource = new MatTableDataSource(data);
    this.dataSource.paginator = this.paginator;
  },
  (err:any) => {


console.log(err);

  });

  }
  handleDeleteAction(values: any) {
    const dialogConfig=new MatDialogConfig();
    dialogConfig.data={
      message:"Are you sure you want to delete this Course ?",
      confirmation:true,
    }
    const dialogRef=this.dialog.open(ConfirmationComponent,dialogConfig);
    const sub=dialogRef.componentInstance.onEmitStatusChange.subscribe(()=>{

      this.deleteCourse(values.id);
      dialogRef.close();




    });


        }

        deleteCompany(id: any) {
          this.adminService.deleteCompany(id).subscribe(
      (reponse:any)=>{
        console.log("course deleted succefully");

      this.getAllCourses();
      this.snackbar.openSnackBar("Course deleted succefully ","success");

       // this.route.navigate(['/profilFormateur/courses'])

      },
      (error:any)=>{
        console.log(error);

        console.log("insde the error in the delete course   ");

      console.log(error);


      }

          );
        }
}
